import React, { Component, useEffect, useId } from 'react';
import '../style/main.css';
import StartMenu from './StartMenu';


export default function TaskBar(){

    const [time, setTime] = React.useState("")
    const [toggle, setToggle] = React.useState(false)
    function getTime(){
        var t = new Date().getHours() + ":" + new Date().getMinutes()
        setTime(t)
        console.log(time)

    }

    function toggleStartMenu(){
        const start = document.getElementsByClassName('StartMenu')

        if ( toggle === false){
            setToggle(true)
            start.setAttribute('visibility', 'hidden')

        }else{
            setToggle(false)
            start.setAttribute('visibility', 'visible')

        }

         console.log(toggle)
    }

    useEffect (() =>{
        getTime()


    })



    return(
        <div className='TaskBar'>
            <StartMenu/>
            <div className='StartMenuButton' onClick={toggleStartMenu}>
                <strong className='title'>start</strong>
            </div>
        <div className='TaskBarActivity'></div>

        <div className='TaskBarEnd'>
        <p>{time}</p>
        </div>
        </div>



    )
}
