import React, { Component, useEffect } from 'react';
import './calculator.css';

export default function Calculator(props){
    const [output, setOutput] = React.useState('');
    const amount = React.useRef([]);
    const ops = React.useRef('');
    const [count, setCount] = React.useState(0);

    const btnValues = [
        ["C", "+-", "%", "/"],
        [7, 8, 9, "X"],
        [4, 5, 6, "-"],
        [1, 2, 3, "+"],
        [0, ".", "="],
      ];

    function sum(){

        var num = 0;
        var size = amount.current.length 
        let temp ;
        if (size === 1){
            num = amount.current[0]
        }else{

            for (let i = 0; i < size; i++){

                temp = amount.current[i].replace(",","")
                num += parseFloat(temp)
            } 

        amount.current = []
        amount.current.push(num)

        setOutput(num)
        }





    }

    function multiply(){
        var num =1;
        var size = amount.current.length 
        let temp ;
        console.log(amount.current)
        for (let i = 0; i < size; i++){

            temp = amount.current[i].replace(",","")

            num *= parseFloat(temp)
        }       
            setOutput(num)

    }

    function getInput(btn){

        if (btn === "C"){
            setOutput([]) 
            amount.current = []
        }else if ((btn === "+")||(btn === "X")){

           amount.current.push(output.toString())
           ops.current = btn
           if(ops.current === "+"){
            sum()
             }else if (ops.current === "X"){
            multiply()
            }

            amount.current = []
            amount.current.push(output.toString())


            setOutput([])
        }else if(btn === "=" ){

            amount.current.push(output.toString())
            console.log(amount.current)
            if(ops.current === "+"){
                sum()
            }else if (ops.current === "X"){
                multiply()
            }


        }else{
            setOutput(current => [...current, btn])
       }


    }

    const AppStyle = {
        visibility : props.visibility ? "visible" : "hidden",

       }

    function minimise(){
        console.log("mini")
        props.toggle()
    }

    function close(){
        props.close()
    }


    useEffect(()=>{

    },[amount])



    return(

        <div className='calculator' style={AppStyle}>
            <div className='top-menu'>
                <h5> Calculator</h5>
                <div className='top-menu-buttons'>
                <button className='minimise'onClick={minimise}>-</button>
                <button className='maximise'>[ ]</button>
                <button className='close' onClick={close}>X</button>
                </div>
            </div>
        <div className='screen'><p>{output}</p></div>
        <div className='inputs'>
        <div className='num-buttons'>

        {
          btnValues.flat().map((btn, i) => {
            return (
              <button
                key={i}
                className={btn === "=" ? "equals" : ""}

                onClick={() => {
                getInput(btn)    
                }}
              >{btn}</button>
            );
          })
        }
        </div>


        </div>

        </div>
    )
}
