import React, { Component } from 'react';
import './calculator.css';

export default function Calculator(props){
    const [output, setOutput] = React.useState('');


    function calculate(){

    }


    function getInput(event){

       var value = event.target.innerText;

       console.log(value)

    }

    const AppStyle = {
        visibility : props.visibility ? "visible" : "hidden",

       }
    console.log(props.visibility)
    function minimise(){
        console.log("mini")
        props.toggle()
    }

    function close(){
        props.close()
    }




    return(

        <div className='calculator' style={AppStyle}>
            <div className='top-menu'>
                <h5> Calculator</h5>
                <div className='top-menu-buttons'>
                <button className='minimise'onClick={minimise}>-</button>
                <button className='maximise'>[ ]</button>
                <button className='close' onClick={close}>X</button>
                </div>
            </div>
        <div className='screen'><p>{output}</p></div>
        <div className='inputs'>
        <div className='num-buttons'>
        <button onClick={getInput}>1</button>
        <button>2</button>
        <button>3</button>
        <button>4</button>
        <button>5</button>
        <button>6</button>
        <button>7</button>
        <button>8</button>
        <button>9</button>
        <button>0</button>

        </div>

        <div className='op-buttons'>
        <button>=</button>
        <button>+</button>
        <button>-</button>
        <button>x</button>

        </div>
        </div>

        </div>
    )
}
