import React, { Component } from 'react';
import StartMenu from './components/StartMenu';
import TaskBar from './components/TaskBar';
import Window from './components/Window';
import './style/main.css';
import axios from 'axios';

function App() {

  const [task, setTask] = React.useState('')
  const [isWindowVisible, setWindowVisible] = React.useState('')

  const toggleVisible = () =>{
      if (isWindowVisible){
        setWindowVisible(false)
      }else{
        setWindowVisible(true)
      }
  }

  const getTask = (data) => {
        setTask(data)
        setWindowVisible(true)
  }

  const closeWindow = () =>{
    setTask('')
    setWindowVisible(false)
  }

  async function getWeather(){

   try{
    const res = await axios.get('http://api.weatherapi.com/v1/current.json?key=51fb9fb1bcad4441b0c213452230105&q=London')
    console.log(res.data)
   }catch(e){
    console.log(e)
   }

  }

  React.useEffect(()=>{

    getWeather()
  })

  return (
    <div className="App">
      <Window close = {closeWindow}  visibility = {isWindowVisible} title = {task} toggle = {toggleVisible}/>
      <TaskBar visibility = {toggleVisible} task = {task} getTask = {getTask}/>
    </div>
  );
}

export default App;
